import React from 'react'
import PropTypes from 'prop-types';
//
import { Container, Row, Col } from 'react-bootstrap';

export const Messages = ({ msgs }) => {
    console.log({ ...msgs });
    // console.log(description); // subject: 'sub', issueDate: 'date', description:

    if (msgs.length < 1) { return (<div></div>) }

    return (
        <Container className="messages-container" >
            {
                msgs.map((msg, i) => {
                    const { date, message, messageBy } = msg;
                    return (
                        // <Row className="mb-100" border="warning" style={{ width: '18rem' }} key={i}>
                        <Row key={i} className="mt-2 mb-2 border">
                            <Col sm={4}>
                                <div> <b> {messageBy} </b></div>
                                <div> {date} </div>
                            </Col>
                            <Col sm={8}>
                                {message}
                            </Col>
                        </Row>
                    )
                })
            }

        </Container>
    )
}

Messages.propTypes = {
    msgs: PropTypes.array.isRequired,
    // subject: PropTypes.string.isRequired 
    // onSubmit: PropTypes.func.isRequired,
}
