    const onSubmit = (e) => {
        e.preventDefault();
        if (!email || !password) {
            alert('please enter email')
        }
        // setEmail(email);
        // setPassword(password);
        console.log(email, password);
    }


    const loginFormProps = {
        handleChange,
        onSubmit,
        email,
        password
    }

    return (
        <div className="entry-page">
            <Jumbotron >
                <LoginForm {...loginFormProps} />
                <PasswordReset />
            </Jumbotron>

        </div>
    )
}

export default Entry
