import React, { useState, useEffect } from 'react'
import { NewTicketForm } from '../../components/newTicket/NewTicketForm'
//
import { Container, Row, Col } from 'react-bootstrap';
import { PageBreadcrumb } from '../../components/breadcrumb/Breadcrumb';
import './newTicket.css';
import { validateNewTicketForm } from '../../utils/newTicketValidation';

export const NewTicket = () => {
    const [formData, setFormData] = useState({ subject: '', issueDate: '', description: '' });
    const { subject, issueDate, description } = formData;
    const [formErrors, setFormErrors] = useState({ subject: false, description: false })

    //
    useEffect(() => {
    }, [formData])


    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    }
    const onSubmit = (e) => {
        e.preventDefault();
        console.log(subject, issueDate, description);

        console.log('formErrors are 1 ', formErrors);
        if (validateNewTicketForm(subject)) {
            setFormErrors({ ...formErrors, subject: true })
        }
        console.log('formErrors are 2 ', formErrors);
        if (validateNewTicketForm(description)) {
            setFormErrors({ ...formErrors, description: true })
        }
        console.log('formErrors are 3 ', formErrors);
        console.log(subject, issueDate, description);
        setFormData({ subject: '', issueDate: '', description: '' });
    }

    const newTicketFormProps = {
        formData,
        handleChange,
        onSubmit,
        formErrors,
    }




    return (
        <Container className="newTicket-container" >
            <Row>
                <Col>
                    <PageBreadcrumb pageName='New ticket' />
                </Col>
            </Row>
            <Row>
                <Col className="text-center mt-5 mb-2" >
                    <NewTicketForm {...newTicketFormProps} />
                </Col>
            </Row>
        </Container>
    )
}
