import React, { useState } from 'react'
import { Jumbotron } from 'react-bootstrap'
import { Button } from 'react-bootstrap';
import './entry.css'
//
import { LoginForm } from '../components/login/LoginForm';
import { PasswordReset } from '../components/passwordReset/PasswordReset';


const Entry = () => {
    // const [email, setEmail] = useState('');
    // const [password, setPassword] = useState(''); 
    const [formData, setFormData] = useState({ email: '', password: '' });
    const { email, password } = formData;
    const [loadForm, setLoadForm] = useState('login')


    // const handleChange = (e) => {
    //     switch (e.target.name) {
    //         case 'email':
    //             setEmail(e.target.value);
    //             break;
    //         case 'password':
    //             setPassword(e.target.value);
    //             break;
    //         default:
    //             break;
    //     }
    // }
    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    }

    const onSubmit = (e) => {
        e.preventDefault();
        if (!email || !password) {
            alert('please enter email')
        }
        // setEmail(email);
        // setPassword(password);
        console.log(email, password);
    }

    const showForm = () => {
        if (loadForm == 'login')
            setLoadForm('passwordReset')
        if (loadForm == 'passwordReset')
            setLoadForm('login')
    }


    const loginFormProps = {
        handleChange,
        onSubmit,
        email,
        password
    }

    return (
        <div className="entry-page">
            <Jumbotron >
                <div className="login-form">
                    {(loadForm == 'login') && <LoginForm {...loginFormProps} />}
                    {(loadForm == 'passwordReset') && <PasswordReset />}
                    <hr />
                    <p onClick={showForm} className='center-btn'>
                        {(loadForm == 'passwordReset') ? 'Login' : 'Reset password'}
                    </p>
                    <Button > {(loadForm == 'passwordReset') ? 'Login' : 'Reset password'}</Button>
                </div>
            </Jumbotron>

        </div>
    )
}

export default Entry
